mapper.update:
UPDATE notification
user_id = #{userId}, description = #{description}, is_read = #{isRead}, send_time = #{sendTime}, deleted_at = #{deletedAt}
WHERE id = #{id}

mapper.select:
SELECT n.*, u.username as username from notification n
left join user u on n.user_id = u.id


        and n.id = #{id}


        and n.user_id = #{userId}


        and n.description like CONCAT('%', #{description}, '%')


        and u.username like CONCAT('%', #{username}, '%')


        and n.is_read = #{isRead}


        and n.send_time = #{sendTime}


        and n.deleted_at = #{deletedAt}


ORDER BY n.send_time DESC

mapper.update:
UPDATE notification 
SET deleted_at = NOW()
WHERE id = #{id}
